where recommended = #{recommended,jdbcType=VARCHAR}
  and recom_col = #{recomCol,jdbcType=VARCHAR}
  and recom_source = #{recomSource,jdbcType=VARCHAR}
  and recom_source_col = #{recomSourceCol,jdbcType=VARCHAR}


